/**
 * HAI3 Application Component
 *
 * Renders the Layout with MFE screen container.
 * Layout components are generated by `hai3 scaffold layout` and owned by the project.
 *
 * HAI3Provider (in main.tsx) handles:
 * - Redux Provider setup
 * - HAI3 context (app instance)
 *
 * Layout handles:
 * - Header, Menu, Footer, Sidebar rendering
 * - Theme-aware styling via hooks
 *
 * MfeScreenContainer handles:
 * - MFE domain registration
 * - MFE extension loading and mounting
 * - DOM container for MFE content
 *
 * StudioOverlay (dev mode only):
 * - Development tools for theme/screenset switching
 * - Language selection
 * - API mode toggle (services register their own mocks)
 */

import { Layout } from '@/app/layout';
import { StudioOverlay } from '@hai3/studio';
import { MfeScreenContainer } from '@/app/mfe/MfeScreenContainer';

function App() {
  return (
    <>
      <Layout>
        <MfeScreenContainer />
      </Layout>
      <StudioOverlay />
    </>
  );
}

export default App;
